function calculateTotals(){var e=0,t=document.querySelectorAll(".tv");t.forEach(l=>{e+=1;var n=document.querySelector('input[name="'+e+'_total_value"]'),a=document.querySelector('input[name="'+e+'_net_value"]'),u=document.querySelector('input[name="'+e+'_vat"]');n.value=Number(a.value)+Number(u.value)})}function addVAT(){var e=document.querySelector("#vatSetting");if(e.value=="yes"){var t=0,l=document.querySelectorAll(".vat");l.forEach(n=>{t+=1;var a=document.querySelector('input[name="'+t+'_net_value"]'),u=document.querySelector('input[name="'+t+'_vat"]');a.value&&(console.log(a.value),u.value=(parseFloat(a.value)*.2).toFixed(2))})}}function calculate(){var e=document.querySelectorAll(".net");e.forEach(t=>{t.addEventListener("change",function(){addVAT(),calculateTotals()})})}calculate();
