function calculateTotals(){let e=0;document.querySelectorAll(".tv").forEach(n=>{e+=1;let a=document.querySelector('input[name="'+e+'_total_value"]'),l=document.querySelector('input[name="'+e+'_net_value"]'),u=document.querySelector('input[name="'+e+'_vat"]');a.value=Number(l.value)+Number(u.value)})}function addVAT(){if(document.querySelector("#vatSetting").value=="yes"){let t=0;document.querySelectorAll(".vat").forEach(a=>{t+=1;let l=document.querySelector('input[name="'+t+'_net_value"]'),u=document.querySelector('input[name="'+t+'_vat"]');l.value&&(console.log(l.value),u.value=(parseFloat(l.value)*.2).toFixed(2))})}}function calculate(){document.querySelectorAll(".net").forEach(t=>{t.addEventListener("change",function(){addVAT(),calculateTotals()})})}calculate();
